mapper.select:
select

from sys_menu m

    1 = 1

        and m.menu_name like concat('%', #{menuName}, '%')


        and m.visible = #{visible}


        and m.status = #{status}


        and m.menu_type = #{menuType}


order by m.parent_id, m.menu_sort

select

from sys_menu m
where m.id = #{menuId}

select count(1)
from sys_menu
where parent_id = #{menuId}

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, menu_type = #{menuType}, menu_sort = #{orderNum}, path = #{path}, component = #{component}, query_param = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = now()
where id = #{menuId}

mapper.select:
select

from sys_menu m
where m.menu_name = #{menuName}
  and m.parent_id = #{parentId}
limit 1
